import React, { useState, useEffect, useRef } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import '../css/Solicitud.css'

const MedicalRequestForm = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [location, setLocation] = useState({
    region: '',
    address: '',
    coordinates: null,
    confirmed: false
  });
  const [selectedMedications, setSelectedMedications] = useState([]);
  const [priority, setPriority] = useState('');
  const [medications, setMedications] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const [marker, setMarker] = useState(null);

  // Regiones disponibles
  const regions = [
    { value: 'amazonas', label: 'Amazonas' },
    { value: 'cusco', label: 'Cusco' },
    { value: 'puno', label: 'Puno' },
    { value: 'arequipa', label: 'Arequipa' }
 ];

  // Dolores/medicamentos disponibles
  const availablePains = [
    'Dolor de cabeza',
    'Fiebre',
    'Dolor estomacal',
    'Resfrío común',
    'Presión alta',
    'Diabetes',
    'Dolor muscular',
    'Alergias',
    'Infección urinaria',
    'Dolor de garganta'
  ];

  // Inicializar mapa con Leaflet (gratuito)
useEffect(() => {
  // Solo ejecutar si no hay mapa ya inicializado
  if (map || !mapRef.current) return;

  const initializeMap = () => {
    // Cargar CSS de Leaflet
    if (!document.querySelector('link[href*="leaflet"]')) {
      const link = document.createElement('link');
      link.rel = 'stylesheet';
      link.href = 'https://unpkg.com/leaflet@1.9.4/dist/leaflet.css';
      document.head.appendChild(link);
    }

    // Cargar JavaScript de Leaflet
    if (!window.L) {
      const script = document.createElement('script');
      script.src = 'https://unpkg.com/leaflet@1.9.4/dist/leaflet.js';
      script.onload = () => {
        createMap();
      };
      document.head.appendChild(script);
    } else {
      createMap();
    }
  };

  const createMap = () => {
    // Verificar que el contenedor existe y no está ya inicializado
    if (!mapRef.current || mapRef.current._leaflet_id) {
      return;
    }

    try {
      // Limpiar completamente el contenedor
      mapRef.current.innerHTML = '';
      
      // Crear mapa centrado en todo Perú
      const mapInstance = window.L.map(mapRef.current).setView([-9.189967, -75.015152], 6);
      
      // Agregar capa de OpenStreetMap (gratuita)
      window.L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '© OpenStreetMap contributors'
      }).addTo(mapInstance);

      setMap(mapInstance);

      // Agregar evento de click al mapa
      mapInstance.on('click', async (e) => {
        const { lat, lng } = e.latlng;
        
        // Remover marcador anterior
        if (marker) {
          mapInstance.removeLayer(marker);
        }
        
        // Crear nuevo marcador
        const newMarker = window.L.marker([lat, lng]).addTo(mapInstance);
        setMarker(newMarker);
        
        // Obtener dirección usando Nominatim (servicio gratuito de OpenStreetMap)
        try {
          const response = await fetch(
            `https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lng}&addressdetails=1`
          );
          const data = await response.json();
          
          if (data && data.display_name) {
            setLocation(prev => ({
              ...prev,
              address: data.display_name,
              coordinates: { lat, lng }
            }));
          }
        } catch (error) {
          console.error('Error al obtener dirección:', error);
          setLocation(prev => ({
            ...prev,
            address: `Coordenadas: ${lat.toFixed(6)}, ${lng.toFixed(6)}`,
            coordinates: { lat, lng }
          }));
        }
      });
    } catch (error) {
      console.error('Error al crear mapa:', error);
    }
  };

  initializeMap();
}, []); // Sin dependencias para que solo se ejecute una vez

// Limpiar mapa al desmontar componente
useEffect(() => {
  return () => {
    if (map) {
      try {
        map.remove();
      } catch (error) {
        console.error('Error al limpiar mapa:', error);
      }
      setMap(null);
      setMarker(null);
    }
  };
}, [map]);

  // Obtener ubicación actual del usuario
  const getCurrentLocation = () => {
    setLoading(true);
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const lat = position.coords.latitude;
          const lng = position.coords.longitude;

        if (map && window.L && map.getContainer()) {
            map.setView([lat, lng], 15);
            
            // Remover marcador anterior
            if (marker) {
              map.removeLayer(marker);
            }
            
            // Crear nuevo marcador
            const newMarker = window.L.marker([lat, lng]).addTo(map);
            setMarker(newMarker);
            
            // Obtener dirección
            try {
              const response = await fetch(
                `https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lng}&addressdetails=1`
              );
              const data = await response.json();
              
              if (data && data.display_name) {
                setLocation(prev => ({
                  ...prev,
                  address: data.display_name,
                  coordinates: { lat, lng }
                }));
              }
            } catch (error) {
              console.error('Error al obtener dirección:', error);
              setLocation(prev => ({
                ...prev,
                address: `Coordenadas: ${lat.toFixed(6)}, ${lng.toFixed(6)}`,
                coordinates: { lat, lng }
              }));
            }
          }
          setLoading(false);
        },
        (error) => {
          setError('No se pudo obtener la ubicación actual');
          setLoading(false);
        }
      );
    } else {
      setError('La geolocalización no está soportada en este navegador');
      setLoading(false);
    }
  };

  // Cargar medicamentos desde la API
  useEffect(() => {
    const fetchMedications = async () => {
      try {
        const response = await fetch('http://localhost:8081/api/medicamentos');
        const data = await response.json();
        console.log('Medicamentos cargados:', data); // Para debug
        setMedications(data);
      } catch (err) {
        console.error('Error al cargar medicamentos:', err);
        setError('Error al cargar medicamentos');
      }
    };
    fetchMedications();
  }, []);

  // Manejar selección de medicamentos - VERSIÓN CORREGIDA
  const handleMedicationSelect = (pain) => {
    console.log('Dolor seleccionado:', pain);
    console.log('Medicamentos disponibles:', medications);
    
    // Verificar si ya existe una selección para este dolor
    const existing = selectedMedications.find(item => item.pain === pain);
    if (existing) {
      console.log('Ya existe selección para este dolor');
      return;
    }

    // Buscar medicamentos que coincidan con el dolor
    // Usamos diferentes formas de comparar por si hay variaciones en los nombres
    const medicationForPain = medications.filter(med => {
      // Comparación exacta
      if (med.dolencia === pain) return true;
      
      // Comparación sin distinción de mayúsculas/minúsculas
      if (med.dolencia && med.dolencia.toLowerCase() === pain.toLowerCase()) return true;
      
      // Comparación parcial (contiene)
      if (med.dolencia && med.dolencia.toLowerCase().includes(pain.toLowerCase())) return true;
      
      return false;
    });

    console.log('Medicamentos encontrados para', pain, ':', medicationForPain);

    if (medicationForPain.length > 0) {
      // Tomar el primer medicamento disponible
      setSelectedMedications(prev => [...prev, {
        pain,
        medication: medicationForPain[0],
        quantity: 1
      }]);
    } else {
      // Si no se encuentra medicamento específico, crear uno genérico
      // Esto es temporal para testing - en producción deberías manejar esto diferente
      const genericMedication = {
        id: Date.now(), // ID temporal
        nombreComercial: `Medicamento para ${pain}`,
        formaAdministracion: 'Tableta',
        dolencia: pain
      };
      
      setSelectedMedications(prev => [...prev, {
        pain,
        medication: genericMedication,
        quantity: 1
      }]);
      
      console.warn('No se encontró medicamento específico para', pain, '. Usando medicamento genérico.');
    }
  };

  // Actualizar cantidad de medicamento
  const updateMedicationQuantity = (pain, quantity) => {
    const newQuantity = parseInt(quantity);
    if (newQuantity > 0) {
      setSelectedMedications(prev => 
        prev.map(item => 
          item.pain === pain ? { ...item, quantity: newQuantity } : item
        )
      );
    }
  };

  // Remover medicamento
  const removeMedication = (pain) => {
    setSelectedMedications(prev => prev.filter(item => item.pain !== pain));
  };

  // Enviar solicitud
  const handleSubmit = async () => {
    setLoading(true);
    
    try {
      // Obtener datos del usuario logueado
      const userRole = localStorage.getItem('rol');
      const userName = localStorage.getItem('nombre');
      
      if (!userRole || !userName) {
        setError('Usuario no autenticado');
        setLoading(false);
        return;
      }

      // Crear solicitud principal
      const solicitudData = {
        usuarioId: 1, // Esto debería venir del sistema de autenticación
        prioridad: priority,
        destino: location.address,
        region: location.region,
        estadoSolicitud: 'pendiente'
      };

      const solicitudResponse = await fetch('http://localhost:8081/api/solicitudes', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(solicitudData),
      });

      if (!solicitudResponse.ok) {
        throw new Error('Error al crear solicitud');
      }

      const solicitud = await solicitudResponse.json();

      // Crear entradas en solicitud_medicamentos
      const medicamentosPromises = selectedMedications.map(async (item) => {
        const solicitudMedicamentoData = {
          solicitud: { id: solicitud.id },
          medicamento: { id: item.medication.id },
          cantidad: item.quantity
        };

        return fetch('http://localhost:8081/api/solicitud-medicamentos', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(solicitudMedicamentoData),
        });
      });

      await Promise.all(medicamentosPromises);

      alert('Solicitud enviada exitosamente');
      
      // Resetear formulario
      setCurrentStep(1);
      setLocation({ region: '', address: '', coordinates: null, confirmed: false });
      setSelectedMedications([]);
      setPriority('');
      
    } catch (err) {
      setError('Error al enviar la solicitud: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const nextStep = () => {
    if (currentStep < 4) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  const canProceedStep1 = location.region && location.address && location.confirmed;
  const canProceedStep2 = selectedMedications.length > 0;
  const canProceedStep3 = priority;

  return (
    <div className="medical-form-container">
      <div className="form-container">
        <div className="header">
          <h1 className="title">Solicitud de Medicamentos</h1>
          <div className="progress-bar">
            {[1, 2, 3, 4].map(step => (
              <div
                key={step}
                className={`progress-step ${step <= currentStep ? 'active' : ''}`}
              >
                {step}
              </div>
            ))}
          </div>
        </div>

        {error && (
          <div className="error-message">
            {error}
          </div>
        )}

        {/* Paso 1: Ubicación */}
        {currentStep === 1 && (
          <div className="step-container">
            <h2 className="step-title">Selecciona tu Ubicación</h2>
            
            <div className="region-selector">
              <label className="label">Región:</label>
              <select
                value={location.region}
                onChange={(e) => setLocation(prev => ({ ...prev, region: e.target.value }))}
                className="select"
              >
                <option value="">Selecciona una región</option>
                {regions.map(region => (
                  <option key={region.value} value={region.value}>
                    {region.label}
                  </option>
                ))}
              </select>
            </div>

            <div className="map-container">
              <div className="map-controls">
                <button
                  type="button"
                  onClick={getCurrentLocation}
                  disabled={loading}
                  className="location-button"
                >
                  {loading ? 'Obteniendo ubicación...' : 'Usar mi ubicación actual'}
                </button>
                <p className="map-instructions">
                  📍 Haz clic en el mapa para seleccionar tu ubicación de entrega
                </p>
              </div>
              
              <div ref={mapRef} className="map"></div>
              
              {location.address && (
                <div className="address-confirmation">
                  <p className="address-text">
                    <strong>Dirección seleccionada:</strong><br />
                    {location.address}
                  </p>
                  <div className="confirmation-buttons">
                    <button
                      type="button"
                      onClick={() => setLocation(prev => ({ ...prev, confirmed: true }))}
                      className={`button ${location.confirmed ? 'confirmed-button' : 'confirm-button'}`}
                    >
                      {location.confirmed ? '✓ Confirmado' : 'Confirmar ubicación'}
                    </button>
                  </div>
                </div>
              )}
            </div>

            <div className="navigation-buttons">
              <button
                type="button"
                onClick={nextStep}
                disabled={!canProceedStep1}
                className={`button next-button ${!canProceedStep1 ? 'disabled-button' : ''}`}
              >
                Siguiente
              </button>
            </div>
          </div>
        )}

        {/* Paso 2: Selección de Medicamentos - VERSIÓN MEJORADA */}
        {currentStep === 2 && (
          <div className="step-container">
            <h2 className="step-title">Selecciona tus Medicamentos</h2>
            
            {/* Información de debug - quitar en producción */}
            {medications.length === 0 && (
              <div className="debug-info" style={{padding: '10px', backgroundColor: '#f0f0f0', marginBottom: '10px'}}>
                <p>⚠️ No se han cargado medicamentos desde la base de datos.</p>
                <p>Verifica que el endpoint 'http://localhost:8081/api/medicamentos' esté funcionando.</p>
              </div>
            )}
            
            <div className="pain-selector">
              <label className="label">¿Qué dolencias tienes?</label>
              <div className="pain-grid">
                {availablePains.map(pain => (
                  <button
                    key={pain}
                    type="button"
                    onClick={() => handleMedicationSelect(pain)}
                    disabled={selectedMedications.some(item => item.pain === pain)}
                    className={`pain-button ${selectedMedications.some(item => item.pain === pain) ? 'selected' : ''}`}
                  >
                    {pain}
                    {selectedMedications.some(item => item.pain === pain) && (
                      <span style={{marginLeft: '5px'}}>✓</span>
                    )}
                  </button>
                ))}
              </div>
            </div>

            {selectedMedications.length > 0 && (
              <div className="selected-medications">
                <h3 className="subtitle">Medicamentos Seleccionados:</h3>
                {selectedMedications.map(item => (
                  <div key={item.pain} className="medication-item">
                    <div className="medication-info">
                      <strong>{item.medication.nombreComercial}</strong>
                      <span className="medication-pain">Para: {item.pain}</span>
                      <span className="medication-form">
                        Forma: {item.medication.formaAdministracion}
                      </span>
                    </div>
                    <div className="quantity-controls">
                      <label className="quantity-label">Cantidad:</label>
                      <input
                        type="number"
                        min="1"
                        max="10"
                        value={item.quantity}
                        onChange={(e) => updateMedicationQuantity(item.pain, e.target.value)}
                        className="quantity-input"
                      />
                      <button
                        type="button"
                        onClick={() => removeMedication(item.pain)}
                        className="remove-button"
                      >
                        ✕
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}

            <div className="navigation-buttons">
              <button
                type="button"
                onClick={prevStep}
                className="button prev-button"
              >
                Anterior
              </button>
              <button
                type="button"
                onClick={nextStep}
                disabled={!canProceedStep2}
                className={`button next-button ${!canProceedStep2 ? 'disabled-button' : ''}`}
              >
                Siguiente ({selectedMedications.length} medicamento{selectedMedications.length !== 1 ? 's' : ''} seleccionado{selectedMedications.length !== 1 ? 's' : ''})
              </button>
            </div>
          </div>
        )}

        {/* Paso 3: Prioridad */}
        {currentStep === 3 && (
          <div className="step-container">
            <h2 className="step-title">Selecciona la Prioridad</h2>
            
            <div className="priority-selector">
              {[
                { value: 'alta', label: 'Alta', description: 'Urgente - Necesito el medicamento inmediatamente', color: '#ff4757' },
                { value: 'media', label: 'Media', description: 'Moderada - Puedo esperar algunas horas', color: '#ffa502' },
                { value: 'baja', label: 'Baja', description: 'No urgente - Puedo esperar hasta mañana', color: '#2ed573' }
              ].map(option => (
                <div
                  key={option.value}
                  onClick={() => setPriority(option.value)}
                  className={`priority-option ${priority === option.value ? 'selected' : ''}`}
                  style={{ borderLeft: `4px solid ${option.color}` }}
                >
                  <div className="priority-header">
                    <span className="priority-label">{option.label}</span>
                    {priority === option.value && <span className="checkmark">✓</span>}
                  </div>
                  <p className="priority-description">{option.description}</p>
                </div>
              ))}
            </div>

            <div className="navigation-buttons">
              <button
                type="button"
                onClick={prevStep}
                className="button prev-button"
              >
                Anterior
              </button>
              <button
                type="button"
                onClick={nextStep}
                disabled={!canProceedStep3}
                className={`button next-button ${!canProceedStep3 ? 'disabled-button' : ''}`}
              >
                Siguiente
              </button>
            </div>
          </div>
        )}

        {/* Paso 4: Resumen y Confirmación */}
        {currentStep === 4 && (
          <div className="step-container">
            <h2 className="step-title">Confirma tu Solicitud</h2>
            
            <div className="summary">
              <div className="summary-section">
                <h3 className="summary-title">📍 Ubicación de Entrega</h3>
                <p className="summary-text">
                  <strong>Región:</strong> {regions.find(r => r.value === location.region)?.label}<br />
                  <strong>Dirección:</strong> {location.address}
                </p>
              </div>

              <div className="summary-section">
                <h3 className="summary-title">💊 Medicamentos Solicitados</h3>
                {selectedMedications.map(item => (
                  <div key={item.pain} className="summary-medication">
                    <strong>{item.medication.nombreComercial}</strong> - Cantidad: {item.quantity}<br />
                    <small>Para: {item.pain} | Forma: {item.medication.formaAdministracion}</small>
                  </div>
                ))}
              </div>

              <div className="summary-section">
                <h3 className="summary-title">⚡ Prioridad</h3>
                <p className="summary-text">
                  <span className={`priority-badge ${priority}`}>
                    {priority === 'alta' ? 'Alta' : priority === 'media' ? 'Media' : 'Baja'}
                  </span>
                </p>
              </div>
            </div>

            <div className="navigation-buttons">
              <button
                type="button"
                onClick={prevStep}
                className="button prev-button"
              >
                Anterior
              </button>
              <button
                type="button"
                onClick={handleSubmit}
                disabled={loading}
                className="button submit-button"
              >
                {loading ? 'Enviando...' : 'Enviar Solicitud'}
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default MedicalRequestForm;
