import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import '../css/SimuladorVuelo.css';

const Vuelos = () => {
  const location = useLocation();
  const userId = localStorage.getItem('userId');

  const [vuelos, setVuelos] = useState([]);
  const [progressMap, setProgressMap] = useState({});
  const [distanceMap, setDistanceMap] = useState({});
  const [timeRemainingMap, setTimeRemainingMap] = useState({});
  const [completedMap, setCompletedMap] = useState({});
  const [startTimeMap, setStartTimeMap] = useState({});
  const [durationMap, setDurationMap] = useState({}); // guarda duraciones simuladas
  const [currentTime, setCurrentTime] = useState(Date.now());
  const [loading, setLoading] = useState(false);

  // Actualizar currentTime cada segundo para tiempo real
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTime(Date.now());
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  // Obtener vuelos del usuario
  useEffect(() => {
    if (!userId) return;

    fetch(`http://localhost:8081/api/vuelos/usuario/${userId}`)
      .then(res => {
        if (!res.ok) throw new Error('Error al obtener vuelos');
        return res.json();
      })
      .then(data => {
        const vuelosPendientes = data.filter(vuelo => vuelo.solicitud.estado === 'pendiente');
        setVuelos(vuelosPendientes);
        vuelosPendientes.forEach(vuelo => startFlightSimulation(vuelo.id));
      })
      .catch(err => console.error('Error obteniendo vuelos:', err));
  }, [userId]);

  const startFlightSimulation = (vueloId) => {
    const flightDuration = Math.floor(Math.random() * 60) + 120; // 120 - 180 seg
    const totalDistance = Math.floor(Math.random() * 3) + 3; // 3 - 6 km
    let elapsed = 0;

    setStartTimeMap(prev => ({ ...prev, [vueloId]: Date.now() }));
    setDurationMap(prev => ({ ...prev, [vueloId]: flightDuration }));

    const interval = setInterval(() => {
      elapsed += 1;
      const progressPercent = Math.min((elapsed / flightDuration) * 100, 100);
      const distanceTraveled = (totalDistance * (elapsed / flightDuration)).toFixed(2);

      setProgressMap(prev => ({ ...prev, [vueloId]: progressPercent }));
      setDistanceMap(prev => ({ ...prev, [vueloId]: distanceTraveled }));
      setTimeRemainingMap(prev => ({ ...prev, [vueloId]: flightDuration - elapsed }));

      if (elapsed >= flightDuration) {
        clearInterval(interval);
        setCompletedMap(prev => ({ ...prev, [vueloId]: true }));
      }
    }, 1000);
  };

  const confirmarLlegada = async (vuelo) => {
    try {
      setLoading(true);
      const distanciaKm = parseFloat(distanceMap[vuelo.id]) || 0;
      const estadoFinal = 'exitoso';

      const startTime = startTimeMap[vuelo.id];
      const endTime = Date.now();
      const durationMs = endTime - startTime;
      const durationMin = Math.round(durationMs / 60000);

      const resVuelo = await fetch(
        `http://localhost:8081/api/vuelos/${vuelo.id}/finalizar?estadoFinal=${estadoFinal}&distanciaKm=${distanciaKm}&duracionMinutos=${durationMin}`,
        { method: 'PATCH' }
      );
      if (!resVuelo.ok) throw new Error('Error al finalizar vuelo');

      const resSolicitud = await fetch(
        `http://localhost:8081/api/solicitudes/${vuelo.solicitud.id}/estado?nuevoEstado=confirmado`,
        { method: 'PATCH' }
      );
      if (!resSolicitud.ok) throw new Error('Error al actualizar estado de solicitud');

      alert('✅ Entrega confirmada correctamente');

      setVuelos(prev => prev.filter(v => v.id !== vuelo.id));

      const clearMapKey = (map) => {
        const { [vuelo.id]: _, ...rest } = map;
        return rest;
      };
      setProgressMap(prev => clearMapKey(prev));
      setDistanceMap(prev => clearMapKey(prev));
      setTimeRemainingMap(prev => clearMapKey(prev));
      setCompletedMap(prev => clearMapKey(prev));
      setStartTimeMap(prev => clearMapKey(prev));
      setDurationMap(prev => clearMapKey(prev));

    } catch (err) {
      console.error('Error confirmando llegada:', err);
      alert('❌ ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const formatElapsedTime = (start) => {
    const elapsedMs = currentTime - start;
    const minutes = Math.floor(elapsedMs / 60000);
    const seconds = Math.floor((elapsedMs % 60000) / 1000);
    return `${minutes}m ${seconds}s`;
  };

  return (
    <div className="vuelo-container">
      <h2>Vuelos Actuales 🚁</h2>
      {vuelos.length === 0 ? (
        <p>No hay vuelos registrados.</p>
      ) : (
        vuelos.map(vuelo => {
          const elapsedSeconds = Math.floor((currentTime - (startTimeMap[vuelo.id] || 0)) / 1000);
          const flightDuration = durationMap[vuelo.id] || 1;
          const elapsedPercent = Math.min((elapsedSeconds / flightDuration) * 100, 100);

          return (
            <div key={vuelo.id} className="vuelo-card">
              <p><strong>ID Vuelo:</strong> {vuelo.id}</p>
              <p><strong>Solicitud:</strong> {vuelo.solicitud.id}</p>

              {/* Barra de progreso de simulación */}
              <div className="progress-bar">
                <div className="progress" style={{ width: `${progressMap[vuelo.id] || 0}%` }}></div>
              </div>
              <p>Progreso: {(progressMap[vuelo.id] || 0).toFixed(0)}%</p>

              {/* Nueva barra de tiempo real transcurrido */}
              <div className="progress-bar small-bar">
                <div className="progress" style={{ width: `${elapsedPercent}%`, backgroundColor: '#8bc34a' }}></div>
              </div>
              <p>Tiempo transcurrido: {startTimeMap[vuelo.id] ? formatElapsedTime(startTimeMap[vuelo.id]) : '0m 0s'}</p>

              <p>Distancia: {distanceMap[vuelo.id] || 0} km</p>
              <p>Tiempo restante: {timeRemainingMap[vuelo.id] || 0} seg</p>

              {completedMap[vuelo.id] && (
                <button
                  className="confirm-button"
                  onClick={() => confirmarLlegada(vuelo)}
                  disabled={loading}
                >
                  {loading ? 'Confirmando...' : 'Confirmar llegada de envío'}
                </button>
              )}
            </div>
          );
        })
      )}
    </div>
  );
};

export default Vuelos;
