 // Función corregida para asignar dron según prioridad
const assignDroneByPriority = (priority) => {
  console.log('Drones disponibles:', availableDrones);
  console.log('Prioridad seleccionada:', priority);
  
  // Filtrar drones con batería mínima (reducir requisito para permitir más drones)
  const activeDrones = availableDrones.filter(drone => {
    console.log(`Dron ${drone.identificador}: batería ${drone.nivel_bateria}%`);
    return drone.nivel_bateria >= 30; // Reducido de 50 a 30
  });

  console.log('Drones activos encontrados:', activeDrones.length);

  if (activeDrones.length === 0) {
    console.log('No se encontraron drones con batería suficiente');
    return null;
  }

  // Lógica de asignación por prioridad
  let selectedDrone;
  
  switch (priority) {
    case 'alta':
      // Para prioridad alta, seleccionar el dron con mayor capacidad y batería
      selectedDrone = activeDrones.reduce((best, current) => {
        const bestScore = best.capacidad_kg + (best.nivel_bateria / 100);
        const currentScore = current.capacidad_kg + (current.nivel_bateria / 100);
        return currentScore > bestScore ? current : best;
      });
      break;
      
    case 'media':
      // Para prioridad media, seleccionar dron con capacidad adecuada
      selectedDrone = activeDrones.find(drone => 
        drone.capacidad_kg >= 1.5 && drone.nivel_bateria >= 60
      ) || activeDrones[0];
      break;
      
    case 'baja':
      // Para prioridad baja, cualquier dron disponible
      selectedDrone = activeDrones[0];
      break;
      
    default:
      selectedDrone = activeDrones[0];
  }

  console.log('Dron seleccionado:', selectedDrone);
  return selectedDrone;
};
  // Manejar selección de medicamentos
  const handleMedicationSelect = (pain) => {
    const existing = selectedMedications.find(item => item.pain === pain);
    if (existing) {
      return;
    }

          const medicationForPain = medications.filter(med => {
        if (med.dolores === pain) return true;
        if (med.dolores && med.dolores.toLowerCase() === pain.toLowerCase()) return true;
        if (med.dolores && med.dolores.toLowerCase().includes(pain.toLowerCase())) return true;
        return false;
      });

    if (medicationForPain.length > 0) {
      setSelectedMedications(prev => [...prev, {
        pain,
        medication: medicationForPain[0],
        quantity: 1
      }]);
    } else {
      const genericMedication = {
        id: Date.now(),
        nombre_comercial: `Medicamento para ${pain}`,
        forma_administracion: 'Pastilla',
        dolencia: pain,
        peso: 0.02
      };
      
      setSelectedMedications(prev => [...prev, {
        pain,
        medication: genericMedication,
        quantity: 1
      }]);
    }
  };



  // Actualizar cantidad de medicamento
  const updateMedicationQuantity = (pain, quantity) => {
    const newQuantity = parseInt(quantity);
    if (newQuantity > 0) {
      setSelectedMedications(prev => 
        prev.map(item => 
          item.pain === pain ? { ...item, quantity: newQuantity } : item
        )
      );
    }
  };

  // Remover medicamento
  const removeMedication = (pain) => {
    setSelectedMedications(prev => prev.filter(item => item.pain !== pain));
  };

  // Función handleSubmit corregida
const handleSubmit = async () => {
  if (!userInfo) {
    setError('Usuario no autenticado');
    return;
  }

  setLoading(true);
  
  try {
    console.log('Iniciando proceso de solicitud...');
    console.log('Medicamentos seleccionados:', selectedMedications);
    console.log('Prioridad:', priority);
    
    // 1. Asignar dron según prioridad
    const assignedDrone = assignDroneByPriority(priority);
    
    if (!assignedDrone) {
      setError('No hay drones disponibles en este momento. Intenta más tarde.');
      setLoading(false);
      return;
    }

    console.log('Dron asignado:', assignedDrone);

    // 2. Crear solicitud principal - CORREGIR nombres de campos
    const solicitudData = {
      id_usuario: userInfo.id, // Cambiado de usuario_id a id_usuario
      prioridad: priority,
      destino: location.address,
      region: location.region, // Agregar región que faltaba
      estado_solicitud: 'pendiente', // Cambiado de estado a estado_solicitud
      dron_asignado_id: assignedDrone.id // Agregar dron asignado
    };

    console.log('Enviando solicitud:', solicitudData);

    const solicitudResponse = await fetch('http://localhost:8081/api/solicitudes', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(solicitudData),
    });

    if (!solicitudResponse.ok) {
      const errorData = await solicitudResponse.text();
      throw new Error(`Error al crear solicitud: ${errorData}`);
    }

    const solicitud = await solicitudResponse.json();
    console.log('Solicitud creada:', solicitud);

    // 3. Insertar medicamentos en la tabla solicitud_medicamentos
    for (const medication of selectedMedications) {
      const medicamentoData = {
        solicitud_id: solicitud.id,
        medicamento_id: medication.medication.id,
        cantidad: medication.quantity
      };

      console.log('Insertando medicamento:', medicamentoData);

      await fetch('http://localhost:8081/api/solicitud-medicamentos', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(medicamentoData),
      });
    }

    // 4. Crear registro de vuelo
    const vuelo = {
      solicitud_id: solicitud.id,
      dron_id: assignedDrone.id,
      fecha_inicio: new Date().toISOString(),
      estado_entrega: 'exitoso'
    };

    console.log('Creando vuelo:', vuelo);

    await fetch('http://localhost:8081/api/vuelos', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(vuelo),
    });

    // 5. Calcular peso total de medicamentos
    const pesoTotal = selectedMedications.reduce((total, item) => {
      const peso = item.medication.peso || 0.02; // peso por defecto si no está definido
      return total + (peso * item.quantity);
    }, 0);

    // 6. Mostrar información del éxito
    alert(`
      ✅ Solicitud enviada exitosamente!
      
      📋 ID de Solicitud: ${solicitud.id}
      🚁 Dron asignado: ${assignedDrone.identificador} (${assignedDrone.modelo})
      ⚖️  Capacidad del dron: ${assignedDrone.capacidad_kg} kg
      📦 Peso total medicamentos: ${pesoTotal.toFixed(3)} kg
      🔋 Batería del dron: ${assignedDrone.nivel_bateria}%
      📍 Destino: ${location.address}
      🏛️  Región: ${regions.find(r => r.value === location.region)?.label}
      ⚡ Prioridad: ${priority.toUpperCase()}
      💊 Medicamentos: ${selectedMedications.length}
      
      El dron será despachado pronto hacia tu ubicación.
    `);
    
    // 7. Resetear formulario
    setCurrentStep(1);
    setLocation({ region: '', address: '', coordinates: null, confirmed: false });
    setSelectedMedications([]);
    setPriority('');
    
  } catch (err) {
    console.error('Error completo:', err);
    setError('Error al enviar la solicitud: ' + err.message);
  } finally {
    setLoading(false);
  }
};

  const nextStep = () => {
