import React, { useState, useEffect, useRef } from 'react';
import '../css/SimuladorVuelo.css'


const FlightTracking = ({ flightId }) => {
  const [flightData, setFlightData] = useState(null);
  const [progress, setProgress] = useState(0);
  const [currentDistance, setCurrentDistance] = useState(0);
  const [timeRemaining, setTimeRemaining] = useState(0);
  const [isFlightComplete, setIsFlightComplete] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [confirmingDelivery, setConfirmingDelivery] = useState(false);
  const [deliveryConfirmed, setDeliveryConfirmed] = useState(false);
  
  const intervalRef = useRef(null);
  const startTimeRef = useRef(null);
  
  // Duraci√≥n del vuelo en milisegundos (2-3 minutos)
  const FLIGHT_DURATION = 2.5 * 60 * 1000; // 2.5 minutos
  const TOTAL_DISTANCE = 15.8; // km aproximados
  
  // Cargar datos del vuelo desde la API
  useEffect(() => {
    const loadFlightData = async () => {
      try {
        setLoading(true);
        setError('');
        
        console.log('Cargando datos del vuelo ID:', flightId);
        
        // Obtener datos del vuelo desde la API
        const response = await fetch(`http://localhost:8081/api/vuelos/${flightId}`);
        
        if (!response.ok) {
          throw new Error(`Error ${response.status}: ${response.statusText}`);
        }
        
        const flight = await response.json();
        console.log('Datos del vuelo cargados:', flight);
        
        setFlightData(flight);
        
        // Verificar si el vuelo ya est√° completado
        if (flight.estadoEntrega === 'confirmado') {
          setIsFlightComplete(true);
          setDeliveryConfirmed(true);
          setProgress(100);
          setCurrentDistance(TOTAL_DISTANCE);
          setTimeRemaining(0);
        } else {
          // Iniciar el seguimiento si el vuelo est√° en progreso
          startFlightTracking();
        }
        
      } catch (err) {
        console.error('Error al cargar vuelo:', err);
        setError(`Error al cargar los datos del vuelo: ${err.message}`);
      } finally {
        setLoading(false);
      }
    };
    
    if (flightId) {
      loadFlightData();
    }
    
    // Cleanup
    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [flightId]);
  
  const startFlightTracking = () => {
    startTimeRef.current = Date.now();
    
    intervalRef.current = setInterval(() => {
      const elapsed = Date.now() - startTimeRef.current;
      const progressPercent = Math.min((elapsed / FLIGHT_DURATION) * 100, 100);
      const distanceTraveled = Math.min((elapsed / FLIGHT_DURATION) * TOTAL_DISTANCE, TOTAL_DISTANCE);
      const timeLeft = Math.max(FLIGHT_DURATION - elapsed, 0);
      
      setProgress(progressPercent);
      setCurrentDistance(distanceTraveled);
      setTimeRemaining(timeLeft);
      
      // Completar vuelo cuando llegue al 100%
      if (progressPercent >= 100) {
        setIsFlightComplete(true);
        clearInterval(intervalRef.current);
      }
    }, 100); // Actualizar cada 100ms para suavidad
  };
  
  const formatTime = (milliseconds) => {
    const totalSeconds = Math.ceil(milliseconds / 1000);
    const minutes = Math.floor(totalSeconds / 60);
    const seconds = totalSeconds % 60;
    return `${minutes}:${seconds.toString().padStart(2, '0')}`;
  };
  
  const confirmDelivery = async () => {
    try {
      setConfirmingDelivery(true);
      setError('');
      
      console.log('Confirmando entrega del vuelo:', flightId);
      
      // Calcular datos finales del vuelo
      const finalFlightData = {
        fechaInicio: flightData.fechaInicio,
        fechaFin: new Date().toISOString(),
        duracionMinutos: Math.round(FLIGHT_DURATION / 60000), // Convertir a minutos
        distanciaKm: TOTAL_DISTANCE,
        estadoEntrega: 'confirmado',
        solicitud: flightData.solicitud,
        dron: flightData.dron
      };
      
      console.log('Datos finales del vuelo:', finalFlightData);
      
      // Actualizar vuelo usando PUT
      const flightResponse = await fetch(`http://localhost:8081/api/vuelos/${flightId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(finalFlightData),
      });
      
      if (!flightResponse.ok) {
        const errorText = await flightResponse.text();
        throw new Error(`Error al actualizar vuelo: ${errorText}`);
      }
      
      console.log('Vuelo actualizado exitosamente');
      
      // Actualizar solicitud a confirmado usando PATCH
      const requestResponse = await fetch(`http://localhost:8081/api/solicitudes/${flightData.solicitud.id}/estado?nuevoEstado=confirmado`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        }
      });
      
      if (!requestResponse.ok) {
        const errorText = await requestResponse.text();
        throw new Error(`Error al actualizar solicitud: ${errorText}`);
      }
      
      console.log('Solicitud actualizada exitosamente');
      
      setDeliveryConfirmed(true);
      
      // Redireccionar despu√©s de 3 segundos
      setTimeout(() => {
        window.location.href = '/dashboard.html';
      }, 3000);
      
    } catch (err) {
      console.error('Error al confirmar entrega:', err);
      setError(`Error al confirmar la entrega: ${err.message}`);
    } finally {
      setConfirmingDelivery(false);
    }
  };
  
  const formatDateTime = (dateString) => {
    if (!dateString) return 'N/A';
    
    try {
      const date = new Date(dateString);
      return date.toLocaleString('es-ES', {
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit'
      });
    } catch (error) {
      return 'Fecha inv√°lida';
    }
  };
  
  const getEstadoDisplay = (estado) => {
    const estados = {
      'pendiente': 'Pendiente',
      'en_progreso': 'En Progreso',
      'completado': 'Completado',
      'cancelado': 'Cancelado',
      'confirmado': 'Confirmado'
    };
    return estados[estado] || estado;
  };
  
  const getSolicitudEstadoDisplay = (estado) => {
    const estados = {
      'pendiente': 'Pendiente',
      'asignada': 'Asignada',
      'en_progreso': 'En Progreso',
      'completada': 'Completada',
      'cancelada': 'Cancelada',
      'confirmado': 'Confirmada'
    };
    return estados[estado] || estado;
  };
  
  if (loading) {
    return (
      <div className="min-h-screen bg-gray-100 flex items-center justify-center">
        <div className="bg-white p-8 rounded-lg shadow-md text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto mb-4"></div>
          <p className="text-gray-600">Cargando informaci√≥n del vuelo...</p>
        </div>
      </div>
    );
  }
  
  if (error) {
    return (
      <div className="min-h-screen bg-gray-100 flex items-center justify-center">
        <div className="bg-white p-8 rounded-lg shadow-md text-center max-w-md">
          <div className="text-red-500 text-4xl mb-4">‚ùå</div>
          <h2 className="text-xl font-bold text-gray-800 mb-2">Error</h2>
          <p className="text-gray-600 mb-4">{error}</p>
          <button 
            onClick={() => window.location.reload()}
            className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
          >
            Reintentar
          </button>
        </div>
      </div>
    );
  }
  
  if (deliveryConfirmed) {
    return (
      <div className="min-h-screen bg-gray-100 flex items-center justify-center">
        <div className="bg-white p-8 rounded-lg shadow-md text-center max-w-md">
          <div className="text-green-500 text-6xl mb-4">‚úÖ</div>
          <h2 className="text-2xl font-bold text-gray-800 mb-2">¬°Entrega Confirmada!</h2>
          <p className="text-gray-600 mb-2">Tu solicitud ha sido completada exitosamente.</p>
          <p className="text-sm text-gray-500">Ser√°s redirigido en unos segundos...</p>
        </div>
      </div>
    );
  }
  
  return (
    <div className="min-h-screen bg-gray-100 p-4">
      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <h1 className="text-3xl font-bold text-gray-800 mb-4 flex items-center">
            <span className="mr-2">üöÅ</span>
            Seguimiento de Vuelo
          </h1>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="flex flex-col">
              <span className="text-sm text-gray-500">ID Vuelo</span>
              <span className="font-semibold text-gray-800">{flightData?.id || 'N/A'}</span>
            </div>
            <div className="flex flex-col">
              <span className="text-sm text-gray-500">Dron</span>
              <span className="font-semibold text-gray-800">{flightData?.dron?.identificador || 'N/A'}</span>
            </div>
            <div className="flex flex-col">
              <span className="text-sm text-gray-500">Destino</span>
              <span className="font-semibold text-gray-800">{flightData?.solicitud?.destino || 'N/A'}</span>
            </div>
          </div>
        </div>
        
        {/* Status */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-xl font-bold text-gray-800">
              {!isFlightComplete ? 'üõ´ Dron en Vuelo' : 'üéØ Dron Lleg√≥ al Destino'}
            </h2>
            <div className={`px-3 py-1 rounded-full text-sm font-medium ${
              !isFlightComplete 
                ? 'bg-blue-100 text-blue-800' 
                : 'bg-green-100 text-green-800'
            }`}>
              {!isFlightComplete ? 'En Tr√°nsito' : 'En Destino'}
            </div>
          </div>
          
          {!isFlightComplete && (
            <div className="flex items-center justify-between">
              <span className="text-gray-600">Tiempo estimado de llegada:</span>
              <span className="text-2xl font-bold text-blue-600">{formatTime(timeRemaining)}</span>
            </div>
          )}
        </div>
        
        {/* Progress Bar */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <div className="flex items-center justify-between mb-4">
            <span className="text-lg font-semibold text-gray-800">Progreso del Vuelo</span>
            <span className="text-lg font-bold text-blue-600">{Math.round(progress)}%</span>
          </div>
          
          <div className="relative">
            <div className="w-full bg-gray-200 rounded-full h-4 mb-2">
              <div 
                className="bg-blue-500 h-4 rounded-full transition-all duration-300 ease-out relative"
                style={{ width: `${progress}%` }}
              >
                <div className="absolute -top-2 -right-2 text-2xl">
                  üöÅ
                </div>
              </div>
            </div>
            
            <div className="flex justify-between text-sm text-gray-600 mt-2">
              <span>Origen</span>
              <span>Destino</span>
            </div>
          </div>
        </div>
        
        {/* Metrics */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
          <div className="bg-white rounded-lg shadow-md p-6">
            <div className="flex items-center">
              <div className="text-3xl mr-4">üìè</div>
              <div>
                <div className="text-sm text-gray-500">Distancia Recorrida</div>
                <div className="text-lg font-semibold text-gray-800">
                  {currentDistance.toFixed(1)} km / {TOTAL_DISTANCE} km
                </div>
              </div>
            </div>
          </div>
          
          <div className="bg-white rounded-lg shadow-md p-6">
            <div className="flex items-center">
              <div className="text-3xl mr-4">‚ö°</div>
              <div>
                <div className="text-sm text-gray-500">Velocidad Promedio</div>
                <div className="text-lg font-semibold text-gray-800">
                  {startTimeRef.current 
                    ? ((currentDistance / ((Date.now() - startTimeRef.current) / 1000 / 60 / 60)) || 0).toFixed(1)
                    : '0.0'
                  } km/h
                </div>
              </div>
            </div>
          </div>
          
          <div className="bg-white rounded-lg shadow-md p-6">
            <div className="flex items-center">
              <div className="text-3xl mr-4">üîã</div>
              <div>
                <div className="text-sm text-gray-500">Bater√≠a del Dron</div>
                <div className="text-lg font-semibold text-gray-800">
                  {flightData?.dron?.nivelBateria || 'N/A'}
                </div>
              </div>
            </div>
          </div>
        </div>
        
        {/* Timeline */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <h3 className="text-lg font-semibold text-gray-800 mb-4">Cronolog√≠a del Vuelo</h3>
          
          <div className="relative">
            <div className="absolute left-4 top-0 bottom-0 w-0.5 bg-gray-300"></div>
            
            <div className="space-y-6">
              <div className="flex items-center">
                <div className="flex-shrink-0 w-8 h-8 bg-green-500 rounded-full flex items-center justify-center text-white font-bold text-sm">
                  ‚úì
                </div>
                <div className="ml-4">
                  <div className="font-semibold text-gray-800">Vuelo Iniciado</div>
                  <div className="text-sm text-gray-600">
                    {formatDateTime(flightData?.fechaInicio)}
                  </div>
                </div>
              </div>
              
              <div className="flex items-center">
                <div className={`flex-shrink-0 w-8 h-8 ${progress > 50 ? 'bg-green-500' : 'bg-yellow-500'} rounded-full flex items-center justify-center text-white font-bold text-sm`}>
                  {progress > 50 ? '‚úì' : '‚è≥'}
                </div>
                <div className="ml-4">
                  <div className="font-semibold text-gray-800">En Tr√°nsito</div>
                  <div className="text-sm text-gray-600">
                    {progress > 50 ? 'Completado' : 'En progreso...'}
                  </div>
                </div>
              </div>
              
              <div className="flex items-center">
                <div className={`flex-shrink-0 w-8 h-8 ${isFlightComplete ? 'bg-green-500' : 'bg-gray-400'} rounded-full flex items-center justify-center text-white font-bold text-sm`}>
                  {isFlightComplete ? '‚úì' : '‚è≥'}
                </div>
                <div className="ml-4">
                  <div className="font-semibold text-gray-800">Llegada al Destino</div>
                  <div className="text-sm text-gray-600">
                    {isFlightComplete ? 'Completado' : 'Pendiente'}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
        
        {/* Detalles Adicionales */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <h3 className="text-lg font-semibold text-gray-800 mb-4">Detalles de la Solicitud</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <span className="text-sm text-gray-500">Estado de Solicitud:</span>
              <span className="ml-2 font-medium text-gray-800">
                {getSolicitudEstadoDisplay(flightData?.solicitud?.estadoSolicitud)}
              </span>
            </div>
            <div>
              <span className="text-sm text-gray-500">Prioridad:</span>
              <span className="ml-2 font-medium text-gray-800">
                {flightData?.solicitud?.prioridad || 'N/A'}
              </span>
            </div>
            <div>
              <span className="text-sm text-gray-500">Regi√≥n:</span>
              <span className="ml-2 font-medium text-gray-800">
                {flightData?.solicitud?.region || 'N/A'}
              </span>
            </div>
            <div>
              <span className="text-sm text-gray-500">Estado de Entrega:</span>
              <span className="ml-2 font-medium text-gray-800">
                {getEstadoDisplay(flightData?.estadoEntrega)}
              </span>
            </div>
          </div>
        </div>
        
        {/* Completion Section */}
        {isFlightComplete && (
          <div className="bg-white rounded-lg shadow-md p-6">
            <div className="text-center">
              <div className="text-4xl mb-4">üéâ</div>
              <h3 className="text-xl font-bold text-gray-800 mb-2">¬°El dron ha llegado a tu ubicaci√≥n!</h3>
              <p className="text-gray-600 mb-6">Por favor, confirma que has recibido tu pedido correctamente.</p>
              
              <button 
                className="px-6 py-3 bg-green-500 text-white rounded-lg hover:bg-green-600 disabled:opacity-50 disabled:cursor-not-allowed font-semibold"
                onClick={confirmDelivery}
                disabled={confirmingDelivery}
              >
                {confirmingDelivery ? 'Confirmando...' : 'Confirmar Llegada de Env√≠o'}
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default FlightTracking;
