const getIcon = (type) => {
    switch (type) {
      case 'VIENTO_FUERTE':
        return <Wind size={24} />;
      case 'LLUVIA_INTENSA':
        return <CloudRain size={24} />;
      case 'NIEBLA_DENSA':
        return <Cloud size={24} />;
      case 'GRANIZO':
        return <Snowflake size={24} />;
      case 'INICIANDO':
        return <Sun size={24} />;
      default:
        return <Sun size={24} />;
    }
  };

  const getIconSmall = (type) => {
    switch (type) {
      case 'VIENTO_FUERTE':
        return <Wind size={20} />;
      case 'LLUVIA_INTENSA':
        return <CloudRain size={20} />;
      case 'NIEBLA_DENSA':
        return <Cloud size={20} />;
      case 'GRANIZO':
        return <Snowflake size={20} />;
      case 'INICIANDO':
        return <Sun size={20} />;
      default:
        return <Sun size={20} />;
    }
  };import React, { useState, useEffect } from 'react';
import { AlertTriangle, Cloud, Wind, CloudRain, Sun, Snowflake } from 'lucide-react';

const DroneAlerts = () => {
  const [currentAlert, setCurrentAlert] = useState(null);
  const [alertHistory, setAlertHistory] = useState([]);

  const provinces = [
    'Arequipa', 'Cusco', 'Trujillo', 'Huancayo', 'Piura', 'Iquitos',
    'Chiclayo', 'Ayacucho', 'Tacna', 'Puno', 'Cajamarca', 'Ica'
  ];

  const alertTypes = [
    {
      type: 'VIENTO_FUERTE',
      title: 'Vientos Fuertes',
      message: 'Vientos de hasta 45 km/h detectados',
      severity: 'high',
      icon: Wind,
      color: 'text-orange-600',
      bgColor: 'bg-orange-50 border-orange-200'
    },
    {
      type: 'LLUVIA_INTENSA',
      title: 'Lluvia Intensa',
      message: 'Precipitaciones intensas en ruta',
      severity: 'high',
      icon: CloudRain,
      color: 'text-blue-600',
      bgColor: 'bg-blue-50 border-blue-200'
    },
    {
      type: 'INICIANDO',
      title: 'Dron Iniciando',
      message: 'Preparando sistemas de vuelo',
      severity: 'low',
      icon: Sun,
      color: 'text-blue-600',
      bgColor: 'bg-blue-50 border-blue-200'
    },
    {
      type: 'NIEBLA_DENSA',
      title: 'Niebla Densa',
      message: 'Visibilidad reducida a menos de 100m',
      severity: 'medium',
      icon: Cloud,
      color: 'text-gray-600',
      bgColor: 'bg-gray-50 border-gray-200'
    },
    {
      type: 'GRANIZO',
      title: 'Granizo',
      message: 'Caída de granizo reportada',
      severity: 'high',
      icon: Snowflake,
      color: 'text-purple-600',
      bgColor: 'bg-purple-50 border-purple-200'
    },
    {
      type: 'DESPEJADO',
      title: 'Condiciones Favorables',
      message: 'Clima despejado y sin obstáculos',
      severity: 'low',
      icon: Sun,
      color: 'text-green-600',
      bgColor: 'bg-green-50 border-green-200'
    }
  ];

  const generateRandomAlert = () => {
    const randomProvince = provinces[Math.floor(Math.random() * provinces.length)];
    const randomAlert = alertTypes[Math.floor(Math.random() * alertTypes.length)];
    const timestamp = new Date().toLocaleTimeString();
    
    const alert = {
      id: Date.now(),
      province: randomProvince,
      timestamp,
      ...randomAlert
    };

    setCurrentAlert(alert);
    setAlertHistory(prev => [alert, ...prev.slice(0, 4)]);
  };

  useEffect(() => {
    // Generar primera alerta de inicio
    const initialAlert = {
      id: Date.now(),
      province: provinces[Math.floor(Math.random() * provinces.length)],
      timestamp: new Date().toLocaleTimeString(),
      type: 'INICIANDO',
      title: 'Dron Iniciando',
      message: 'Preparando sistemas de vuelo',
      severity: 'low',
      color: 'text-blue-600',
      bgColor: 'bg-blue-50 border-blue-200'
    };
    
    setCurrentAlert(initialAlert);
    setAlertHistory([initialAlert]);
    
    // Generar alertas cada 10 segundos
    const interval = setInterval(generateRandomAlert, 10000);
    
    return () => clearInterval(interval);
  }, []);

  const getSeverityBadge = (severity) => {
    const badges = {
      low: 'bg-green-100 text-green-800',
      medium: 'bg-yellow-100 text-yellow-800',
      high: 'bg-orange-100 text-orange-800',
      critical: 'bg-red-100 text-red-800'
    };
    
    return badges[severity] || badges.medium;
  };

  const getSeverityText = (severity) => {
    const texts = {
      low: 'NORMAL',
      medium: 'PRECAUCIÓN',
      high: 'ALERTA',
      critical: 'CRÍTICO'
    };
    
    return texts[severity] || 'ALERTA';
  };

  return (
    <div className="max-w-4xl mx-auto p-6 bg-gradient-to-br from-blue-50 to-indigo-100 min-h-screen">
      <div className="text-center mb-8">
        <h1 className="text-3xl font-bold text-gray-800 mb-2">Sistema de Alertas de Dron</h1>
        <p className="text-gray-600">Monitoreo en tiempo real de condiciones climáticas</p>
      </div>

      {/* Alerta Actual */}
      {currentAlert && (
        <div className={`mb-8 p-6 rounded-xl border-2 shadow-lg ${currentAlert.bgColor} transition-all duration-500 transform hover:scale-105`}>
          <div className="flex items-start justify-between mb-4">
            <div className="flex items-center space-x-3">
              <div className={`p-3 rounded-full bg-white shadow-md ${currentAlert.color}`}>
                {getIcon(currentAlert.type)}
              </div>
              <div>
                <h2 className="text-xl font-bold text-gray-800">{currentAlert.title}</h2>
              </div>
            </div>
            <div className="text-right">
              <span className={`inline-block px-3 py-1 rounded-full text-xs font-semibold ${getSeverityBadge(currentAlert.severity)}`}>
                {getSeverityText(currentAlert.severity)}
              </span>
              <p className="text-sm text-gray-500 mt-1">{currentAlert.timestamp}</p>
            </div>
          </div>
          
          <div className="bg-white bg-opacity-50 rounded-lg p-4">
            <p className="text-gray-700 font-medium">{currentAlert.message}</p>
          </div>
        </div>
      )}

      {/* Botón para generar nueva alerta */}
      <div className="text-center mb-8">
        <button
          onClick={generateRandomAlert}
          className="bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-3 px-6 rounded-lg transition-colors duration-200 shadow-md hover:shadow-lg"
        >
          Generar Nueva Alerta
        </button>
      </div>

      {/* Historial de Alertas */}
      <div className="bg-white rounded-xl shadow-lg p-6">
        <h3 className="text-xl font-bold text-gray-800 mb-4 flex items-center">
          <AlertTriangle className="mr-2 text-amber-500" size={20} />
          Historial de Alertas
        </h3>
        
        <div className="space-y-3">
          {alertHistory.length === 0 ? (
            <p className="text-gray-500 text-center py-4">No hay alertas en el historial</p>
          ) : (
            alertHistory.map((alert) => (
              <div key={alert.id} className={`p-4 rounded-lg border ${alert.bgColor} transition-all duration-300`}>
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <div className={alert.color}>
                      {getIconSmall(alert.type)}
                    </div>
                    <div>
                      <p className="font-semibold text-gray-800">{alert.title}</p>
                      <p className="text-sm text-gray-600">{alert.province} - {alert.timestamp}</p>
                    </div>
                  </div>
                  <span className={`px-2 py-1 rounded text-xs font-medium ${getSeverityBadge(alert.severity)}`}>
                    {getSeverityText(alert.severity)}
                  </span>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default DroneAlerts;
