import React, { useEffect, useState } from 'react';

const SimuladorVuelo = ({ vueloId, solicitudId }) => {
  const [progreso, setProgreso] = useState(0);
  const [duracionVuelo, setDuracionVuelo] = useState(null);
  const [distanciaKm, setDistanciaKm] = useState(null);
  const [velocidadKmH, setVelocidadKmH] = useState(null);
  const [finalizado, setFinalizado] = useState(false);
  const [fechaInicio, setFechaInicio] = useState(null);

  useEffect(() => {
    const velocidad = Math.floor(Math.random() * (80 - 30 + 1)) + 30; // km/h
    const duracionMin = Math.floor(Math.random() * (3 - 2 + 1)) + 2; // minutos
    const distancia = (velocidad * (duracionMin / 60)).toFixed(2); // km
    const fechaInicioActual = new Date().toISOString();

    setVelocidadKmH(velocidad);
    setDuracionVuelo(duracionMin);
    setDistanciaKm(distancia);
    setFechaInicio(fechaInicioActual);

    const totalMs = duracionMin * 60 * 1000;
    const intervalo = 1000; // 1 segundo
    const incremento = 100 / (totalMs / intervalo);

    const start = Date.now();

    const timer = setInterval(() => {
      setProgreso(prev => {
        const nuevo = prev + incremento;
        if (nuevo >= 100) {
          clearInterval(timer);
          setFinalizado(true);
          finalizarVuelo(vueloId, duracionMin, distancia, fechaInicioActual);
          return 100;
        }
        return nuevo;
      });
    }, intervalo);

    return () => clearInterval(timer);
  }, [vueloId]);

  const finalizarVuelo = async (vueloId, duracion, distancia, inicio) => {
    const fechaFin = new Date().toISOString();

    try {
      await fetch(`http://localhost:8081/api/vuelos/${vueloId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          fechaInicio: inicio,
          fechaFin,
          duracionMinutos: duracion,
          distanciaKm: parseFloat(distancia),
          estadoEntrega: 'exitoso',
        }),
      });

      await fetch(`http://localhost:8081/api/solicitudes/${solicitudId}/estado?nuevoEstado=completada`, {
        method: 'PATCH'
      });

      console.log('✅ Vuelo finalizado y solicitud marcada como completada');
    } catch (error) {
      console.error('❌ Error al finalizar vuelo:', error);
    }
  };

  return (
    <div className="p-4 bg-white rounded-xl shadow-md w-full max-w-lg mx-auto mt-6">
      <div className="w-full h-40 bg-gray-100 rounded-lg mb-4 flex items-center justify-center">
        <span className="text-gray-400">[Imagen del vuelo aquí]</span>
      </div>

      <h2 className="text-xl font-bold mb-2">Simulando vuelo #{vueloId}</h2>
      <p className="mb-1">Velocidad: <strong>{velocidadKmH} km/h</strong></p>
      <p className="mb-1">Duración estimada: <strong>{duracionVuelo} min</strong></p>
      <p className="mb-1">Distancia estimada: <strong>{distanciaKm} km</strong></p>

      <div className="w-full bg-gray-200 rounded-full h-4 mt-4">
        <div
          className="bg-blue-500 h-4 rounded-full transition-all duration-300"
          style={{ width: `${progreso}%` }}
        />
      </div>

      {finalizado && <p className="mt-4 text-green-600 font-semibold">✅ Vuelo completado</p>}
    </div>
  );
};

export default SimuladorVuelo;
