import React, { useState, useEffect, useRef } from 'react';
import '../css/FlightTracking.css';

const FlightTracking = ({ flightId }) => {
  const [flightData, setFlightData] = useState(null);
  const [progress, setProgress] = useState(0);
  const [currentDistance, setCurrentDistance] = useState(0);
  const [timeRemaining, setTimeRemaining] = useState(0);
  const [isFlightComplete, setIsFlightComplete] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [confirmingDelivery, setConfirmingDelivery] = useState(false);
  const [deliveryConfirmed, setDeliveryConfirmed] = useState(false);
  
  const intervalRef = useRef(null);
  const startTimeRef = useRef(null);
  
  // Duraci√≥n del vuelo en milisegundos (2-3 minutos)
  const FLIGHT_DURATION = 2.5 * 60 * 1000; // 2.5 minutos
  const TOTAL_DISTANCE = 15.8; // km aproximados
  
  // Cargar datos del vuelo
  useEffect(() => {
    const loadFlightData = async () => {
      try {
        setLoading(true);
        
        // Obtener datos del vuelo
        const response = await fetch(`http://localhost:8081/api/vuelos/${flightId}`);
        if (!response.ok) {
          throw new Error('Error al cargar datos del vuelo');
        }
        
        const flight = await response.json();
        setFlightData(flight);
        
        // Iniciar el seguimiento
        startFlightTracking();
        
      } catch (err) {
        console.error('Error al cargar vuelo:', err);
        setError('Error al cargar los datos del vuelo');
      } finally {
        setLoading(false);
      }
    };
    
    if (flightId) {
      loadFlightData();
    }
    
    // Cleanup
    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [flightId]);
  
  const startFlightTracking = () => {
    startTimeRef.current = Date.now();
    
    intervalRef.current = setInterval(() => {
      const elapsed = Date.now() - startTimeRef.current;
      const progressPercent = Math.min((elapsed / FLIGHT_DURATION) * 100, 100);
      const distanceTraveled = Math.min((elapsed / FLIGHT_DURATION) * TOTAL_DISTANCE, TOTAL_DISTANCE);
      const timeLeft = Math.max(FLIGHT_DURATION - elapsed, 0);
      
      setProgress(progressPercent);
      setCurrentDistance(distanceTraveled);
      setTimeRemaining(timeLeft);
      
      // Completar vuelo cuando llegue al 100%
      if (progressPercent >= 100) {
        setIsFlightComplete(true);
        clearInterval(intervalRef.current);
      }
    }, 100); // Actualizar cada 100ms para suavidad
  };
  
  const formatTime = (milliseconds) => {
    const totalSeconds = Math.ceil(milliseconds / 1000);
    const minutes = Math.floor(totalSeconds / 60);
    const seconds = totalSeconds % 60;
    return `${minutes}:${seconds.toString().padStart(2, '0')}`;
  };
  
  const confirmDelivery = async () => {
    try {
      setConfirmingDelivery(true);
      
      // Calcular datos finales
      const finalData = {
        fechaFin: new Date().toISOString(),
        duracionMinutos: Math.round(FLIGHT_DURATION / 60000), // Convertir a minutos
        distanciaKilometros: TOTAL_DISTANCE,
        estadoEntrega: 'confirmado'
      };
      
      // Actualizar vuelo
      const flightResponse = await fetch(`http://localhost:8081/api/vuelos/${flightId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(finalData),
      });
      
      if (!flightResponse.ok) {
        throw new Error('Error al actualizar vuelo');
      }
      
      // Actualizar solicitud a confirmado
      const requestResponse = await fetch(`http://localhost:8081/api/solicitudes/${flightData.solicitud.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          estadoSolicitud: 'confirmado'
        }),
      });
      
      if (!requestResponse.ok) {
        throw new Error('Error al actualizar solicitud');
      }
      
      setDeliveryConfirmed(true);
      
      // Redireccionar despu√©s de 3 segundos
      setTimeout(() => {
        window.location.href = '/dashboard.html'; // o donde quieras redireccionar
      }, 3000);
      
    } catch (err) {
      console.error('Error al confirmar entrega:', err);
      setError('Error al confirmar la entrega');
    } finally {
      setConfirmingDelivery(false);
    }
  };
  
  if (loading) {
    return (
      <div className="flight-container">
        <div className="loading">
          <div className="loading-spinner"></div>
          <p>Cargando informaci√≥n del vuelo...</p>
        </div>
      </div>
    );
  }
  
  if (error) {
    return (
      <div className="flight-container">
        <div className="error">
          <h2>‚ùå Error</h2>
          <p>{error}</p>
          <button onClick={() => window.location.reload()}>Reintentar</button>
        </div>
      </div>
    );
  }
  
  if (deliveryConfirmed) {
    return (
      <div className="flight-container">
        <div className="success-completion">
          <div className="success-icon">‚úÖ</div>
          <h2>¬°Entrega Confirmada!</h2>
          <p>Tu solicitud ha sido completada exitosamente.</p>
          <p>Ser√°s redirigido en unos segundos...</p>
        </div>
      </div>
    );
  }
  
  return (
    <div className="flight-container">
      <div className="flight-header">
        <h1>üöÅ Seguimiento de Vuelo</h1>
        <div className="flight-info">
          <div className="info-item">
            <span className="label">ID Vuelo:</span>
            <span className="value">{flightData?.id}</span>
          </div>
          <div className="info-item">
            <span className="label">Dron:</span>
            <span className="value">{flightData?.dron?.identificador}</span>
          </div>
          <div className="info-item">
            <span className="label">Destino:</span>
            <span className="value">{flightData?.solicitud?.destino}</span>
          </div>
        </div>
      </div>
      
      <div className="flight-status">
        <div className="status-header">
          <h2>
            {!isFlightComplete ? 'üõ´ Dron en Vuelo' : 'üéØ Dron Lleg√≥ al Destino'}
          </h2>
          <div className="status-badge">
            {!isFlightComplete ? 'En Tr√°nsito' : 'En Destino'}
          </div>
        </div>
        
        {!isFlightComplete && (
          <div className="time-remaining">
            <span className="time-label">Tiempo estimado de llegada:</span>
            <span className="time-value">{formatTime(timeRemaining)}</span>
          </div>
        )}
      </div>
      
      <div className="progress-section">
        <div className="progress-header">
          <span>Progreso del Vuelo</span>
          <span>{Math.round(progress)}%</span>
        </div>
        
        <div className="progress-bar">
          <div 
            className="progress-fill" 
            style={{ width: `${progress}%` }}
          ></div>
          <div className="progress-drone">
            üöÅ
          </div>
        </div>
        
        <div className="progress-labels">
          <span>Origen</span>
          <span>Destino</span>
        </div>
      </div>
      
      <div className="flight-metrics">
        <div className="metric">
          <div className="metric-icon">üìè</div>
          <div className="metric-content">
            <div className="metric-label">Distancia Recorrida</div>
            <div className="metric-value">
              {currentDistance.toFixed(1)} km / {TOTAL_DISTANCE} km
            </div>
          </div>
        </div>
        
        <div className="metric">
          <div className="metric-icon">‚ö°</div>
          <div className="metric-content">
            <div className="metric-label">Velocidad Promedio</div>
            <div className="metric-value">
              {((currentDistance / ((Date.now() - startTimeRef.current) / 1000 / 60 / 60)) || 0).toFixed(1)} km/h
            </div>
          </div>
        </div>
        
        <div className="metric">
          <div className="metric-icon">üîã</div>
          <div className="metric-content">
            <div className="metric-label">Bater√≠a del Dron</div>
            <div className="metric-value">
              {flightData?.dron?.nivelBateria || 'N/A'}
            </div>
          </div>
        </div>
      </div>
      
      <div className="flight-timeline">
        <div className="timeline-item completed">
          <div className="timeline-dot"></div>
          <div className="timeline-content">
            <div className="timeline-title">Vuelo Iniciado</div>
            <div className="timeline-time">
              {new Date(flightData?.fechaInicio).toLocaleTimeString()}
            </div>
          </div>
        </div>
        
        <div className={`timeline-item ${progress > 50 ? 'completed' : 'active'}`}>
          <div className="timeline-dot"></div>
          <div className="timeline-content">
            <div className="timeline-title">En Tr√°nsito</div>
            <div className="timeline-time">
              {progress > 50 ? 'Completado' : 'En progreso...'}
            </div>
          </div>
        </div>
        
        <div className={`timeline-item ${isFlightComplete ? 'completed' : ''}`}>
          <div className="timeline-dot"></div>
          <div className="timeline-content">
            <div className="timeline-title">Llegada al Destino</div>
            <div className="timeline-time">
              {isFlightComplete ? 'Completado' : 'Pendiente'}
            </div>
          </div>
        </div>
      </div>
      
      {isFlightComplete && (
        <div className="completion-section">
          <div className="completion-message">
            <h3>üéâ ¬°El dron ha llegado a tu ubicaci√≥n!</h3>
            <p>Por favor, confirma que has recibido tu pedido correctamente.</p>
          </div>
          
          <button 
            className="confirm-delivery-btn"
            onClick={confirmDelivery}
            disabled={confirmingDelivery}
          >
            {confirmingDelivery ? 'Confirmando...' : 'Confirmar Llegada de Env√≠o'}
          </button>
        </div>
      )}
    </div>
  );
};

export default FlightTracking;
